import React,{useState} from 'react';
import arrow from './images/icon-arrow.svg';
import error from './images/icon-error.svg';


const Email = () => {
    const [initial,setInitial] = useState({
        email:'',
        message:'',
        image:''
      })



      function emailType(event){
        const {value} = event.target; 
        setInitial({
            email:value,
            message:'',
            image:''
        })
        event.preventDefault();
          }



          const onSubmit = () => {
            if (initial.email.includes('@') === false || initial.email === ''){
              setInitial({
                message:'Please provide a valid email',
                image:{error}
              })
            }
          }

    return(
        <form className='emailContainer'>
            <input onChange={emailType} type='text' placeholder='Email Address'></input>
            <input onClick={onSubmit} className='arrow' type='image' src={arrow} alt='arrow'/> 
            <img src={initial.image} alt='error'></img>
            <p>{initial.message}</p>
        </form>
    )
}



export default Email;
